if count <= 0:
    return []
if count == 1:
    return [0]
sequence = [0, 1]
for _ in range(2, count):
    sequence.append(sequence[-1] + sequence[-2])
return sequence

import math
if num < 0:
    return False
def is_perfect_square(n):
    root = int(math.sqrt(n))
    return root * root == n
return is_perfect_square(5 * num * num + 4) or is_perfect_square(5 * num * num - 4)
